import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './HomePage.css';
import background from '../assets/background.jpg';

// FontAwesome İkonları
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

interface Favorite {
  id: number;
  name: string;
  description: string;
}

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [favorites, setFavorites] = useState<Favorite[]>([]); // Favoriler
  const [editorChoices, setEditorChoices] = useState<Favorite[]>([]); // Slider için veriler

  // Slider ayarları
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      { breakpoint: 768, settings: { slidesToShow: 1 } },
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
    ],
  };

  // Favorileri API'den çek
  useEffect(() => {
    // `Favorites` tablosundan veriyi al
    fetch("http://localhost:5114/api/Favorites")
      .then((res) => res.json())
      .then((data) => setEditorChoices(data)) // Editörün seçimlerini güncelle
      .catch((err) => console.error("Favori verileri alınamadı:", err));
  }, []);

  const addToFavorites = (item: Favorite) => {
    if (!favorites.some((fav) => fav.id === item.id)) {
      setFavorites([...favorites, item]);
    }
  };

  return (
    <div className="homepage-container" style={{ backgroundImage: `url(${background})` }}>
      <h1 className="homepage-title">Hoşgeldiniz!</h1>
      <h2 className="homepage-subtitle">Ne İzlemek İstersiniz?</h2>
      <h3 className="featured-title">Editörün Seçimleri</h3>

      {/* Slider */}
      <div className="slider-section">
        <Slider {...sliderSettings} className="slider-container">
          {editorChoices.map((item) => (
            <div key={item.id} className="card">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <button
                className="add-favorite-button"
                onClick={() => addToFavorites(item)}
              >
                <FontAwesomeIcon icon={faHeart} className="heart-icon" />
              </button>
            </div>
          ))}
        </Slider>
      </div>

      {/* Kategoriler */}
      <div className="categories-section">
        <button className="category-button" onClick={() => navigate('/diziler')}>
          Diziler
        </button>
        <button className="category-button" onClick={() => navigate('/filmler')}>
          Filmler
        </button>
      </div>

      {/* Favoriler */}
      <div className="favorites-container">
        <h3 className="favorites-title">Favori Listeniz</h3>
        {favorites.length === 0 ? (
          <p className="empty-favorites">Favori listeniz henüz boş.</p>
        ) : (
          <div className="cards-container">
            {favorites.map((favorite) => (
              <div key={favorite.id} className="card">
                <h4>{favorite.name}</h4>
                <p>{favorite.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
