import React, { useEffect, useState } from 'react';
import { getCategories } from '../services/api'; // API çağrısını import ediyoruz
import styles from './CategoriesPage.module.css'; // CSS modülünü import edin

interface Category {
  id: number;
  name: string; // Kategori adı
  description: string; // Kategori açıklaması
}

const CategoriesPage: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const data = await getCategories();
        console.log('Gelen Kategoriler:', data); // Gelen veriyi kontrol etmek için konsola yazdırıyoruz
        setCategories(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchCategories();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Kategoriler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {categories.map((category) => (
          <div key={category.id} className={styles.card}>
            <h2 className={styles.cardTitle}>{category.name}</h2> {/* Kategori adı */}
            <div className={styles.cardHover}>
              <h2 className={styles.hoverTitle}>{category.name}</h2>
              <p className={styles.description}>{category.description || "Açıklama Yok"}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoriesPage;
