import React, { useState } from 'react';

const HomePage: React.FC = () => {
  const [favorites, setFavorites] = useState<string[]>([]);

  // Favori ekleme fonksiyonu
  const addToFavorites = (item: string) => {
    if (!favorites.includes(item)) {
      setFavorites([...favorites, item]);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', textAlign: 'center' }}>
      <h1>Ne İzlemek İstersiniz?</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', marginBottom: '40px' }}>
        {/* Dizi Kutucuğu */}
        <button
          onClick={() => addToFavorites('Favori Bir Dizi')}
          style={{
            backgroundColor: '#8e44ad',
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            padding: '20px 40px',
            cursor: 'pointer',
            fontSize: '18px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          }}
        >
          Dizi
        </button>
        {/* Film Kutucuğu */}
        <button
          onClick={() => addToFavorites('Favori Bir Film')}
          style={{
            backgroundColor: '#e74c3c',
            color: 'white',
            border: 'none',
            borderRadius: '10px',
            padding: '20px 40px',
            cursor: 'pointer',
            fontSize: '18px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          }}
        >
          Film
        </button>
      </div>

      {/* Favori Listesi */}
      <div style={{ textAlign: 'left', marginTop: '20px' }}>
        <h2>Favori Listeniz</h2>
        {favorites.length > 0 ? (
          <ul>
            {favorites.map((item, index) => (
              <li key={index} style={{ fontSize: '18px', marginBottom: '10px' }}>
                {item}
              </li>
            ))}
          </ul>
        ) : (
          <p>Henüz favori eklemediniz. Dizi veya film seçerek favori listenizi oluşturabilirsiniz!</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
