import React, { useState, useEffect, JSX } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Token kontrolü
  useEffect(() => {
    const token = document.cookie
      .split('; ')
      .find(row => row.startsWith('token='))
      ?.split('=')[1]; // Token değerini bul
    setIsAuthenticated(!!token); // Token varsa true, yoksa false
  }, []);

  // Özel Route Bileşeni: Giriş yapılmamışsa Login'e yönlendirme
  const PrivateRoute = ({ element }: { element: JSX.Element }) => {
    const location = useLocation(); // Kullanıcının bulunduğu route

    return isAuthenticated ? (
      element
    ) : (
      <Navigate to="/" replace state={{ from: location }} />
    );
  };

  return (
    <Router>
      <div>
        {isAuthenticated && <Navbar />}

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={() => setIsAuthenticated(true)}
                />
              )
            }
          />
          <Route path="/register" element={<RegisterPage />} />
          {isAuthenticated && (
            <>
              <Route path="/about" element={<AboutPage />} />
              <Route path="/diziler" element={<PrivateRoute element={<SeriesPage />} />} />
              <Route path="/filmler" element={<PrivateRoute element={<MoviesPage />} />} />
              <Route path="/kategoriler" element={<PrivateRoute element={<CategoriesPage />} />} />
              <Route path="/profilim" element={<PrivateRoute element={<ProfilePage />} />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
