import React, { useEffect, useState } from 'react';
import { getMovies } from '../services/api';
import styles from './MoviesPage.module.css';

interface Movie {
  id: number;
  title: string;
  description: string;
  releaseYear: number;
  genre: string;
  imdbRating: number;
  duration: string;
}

const MoviesPage: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const data = await getMovies();
        console.log('Gelen Filmler:', data);
        setMovies(Array.isArray(data) ? data : []); // Verinin dizi olduğundan emin ol
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Filmler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {movies.length > 0 ? (
          movies.map((item) => (
            <div key={item.id} className={styles.card}>
              <h2 className={styles.cardTitle}>{item.title}</h2>
              <div className={styles.cardHover}>
                <h2 className={styles.hoverTitle}>{item.title}</h2>
                <p><strong>Yıl:</strong> {item.releaseYear || "Bilinmiyor"}</p>
                <p><strong>Tür:</strong> {item.genre || "Bilinmiyor"}</p>
                <p><strong>Puan:</strong> {item.imdbRating || "Puan Yok"}</p>
                <p><strong>Süre:</strong> {item.duration || "Süre Yok"}</p>
                <p className={styles.description}>{item.description || "Açıklama Yok"}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Henüz film verisi bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default MoviesPage;
