import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';
import AdminPanel from './pages/AdminPanel'; // Admin paneli sayfası
import api from './services/api'; // Axios instance

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false); // Admin durumu
  const [loading, setLoading] = useState(true); // Sayfa yüklenirken durum

  useEffect(() => {
    const checkAuth = async () => {
      try {
        // Backend'den token doğrulama isteği gönder
        const response = await api.get('/Authentication/Verify');
        setIsAuthenticated(true);
        setIsAdmin(response.data.role === 'Admin'); // Admin olup olmadığını kontrol et
      } catch (err) {
        console.error('Doğrulama başarısız:', err);
        setIsAuthenticated(false);
        setIsAdmin(false);
      } finally {
        setLoading(false); // Yükleme durumu tamamlandı
      }
    };

    checkAuth();
  }, []);

  // Çıkış yapıldığında çağrılan fonksiyon
  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsAdmin(false);
    api.post('/Authentication/Logout').then(() => {
      window.location.href = '/'; // Giriş sayfasına yönlendir
    });
  };

  if (loading) {
    // Yükleme sırasında spinner veya bir ekran gösterebilirsiniz
    return <div>Yükleniyor...</div>;
  }

  return (
    <Router>
      <div>
        {/* Navbar yalnızca giriş yapıldığında gösterilir */}
        {isAuthenticated && <Navbar isAdmin={isAdmin} onLogout={handleLogout} />}

        <div style={{ textAlign: 'center', margin: '20px' }}>
          {/* Admin kullanıcıları için admin paneline yönlendirme */}
          {isAuthenticated && isAdmin && (
            <Link to="/admin-panel">
              <button style={{ padding: '10px 20px', fontSize: '16px' }}>
                Admin Paneline Git
              </button>
            </Link>
          )}
        </div>

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={() => setIsAuthenticated(true)} // Giriş başarılı olduğunda oturum durumunu ayarla
                  onRegisterClick={() => (window.location.href = '/register')}
                />
              )
            }
          />
          {isAuthenticated && (
            <>
              <Route path="/about" element={<AboutPage />} />
              <Route path="/diziler" element={<SeriesPage />} />
              <Route path="/filmler" element={<MoviesPage />} />
              <Route path="/kategoriler" element={<CategoriesPage />} />
              <Route path="/profilim" element={<ProfilePage />} />
              {isAdmin && <Route path="/admin-panel" element={<AdminPanel />} />}
            </>
          )}
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
