import React, { useEffect, useState } from 'react';
import './ProfilePage.css';

const ProfilePage: React.FC = () => {
  const [userEmail, setUserEmail] = useState<string | null>(null);
  const [userName, setUserName] = useState<string | null>(null);

  useEffect(() => {
    // Token'dan kullanıcı emailini çıkarma
    const token = document.cookie
      .split('; ')
      .find((row) => row.startsWith('token='))
      ?.split('=')[1];

    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1])); // JWT'den payload'ı al
        const email = payload["http://schemas.xmlsoap.org/ws/2005/05/identity/claims/name"];
        setUserEmail(email || null);
      } catch (error) {
        console.error("Token çözümlemesinde hata oluştu:", error);
      }
    }
  }, []);

  useEffect(() => {
    // Kullanıcı bilgilerini API'den çekme
    if (userEmail) {
      fetch(`http://localhost:5114/api/Users?email=${userEmail}`)
        .then((res) => {
          if (!res.ok) {
            throw new Error(`HTTP Hatası! Durum: ${res.status}`);
          }
          return res.json();
        })
        .then((data) => {
          setUserName(data.name || "Bilinmiyor");
        })
        .catch((error) => {
          console.error("Kullanıcı bilgisi alınırken hata oluştu:", error);
        });
    }
  }, [userEmail]);

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h1>Profil Bilgileri</h1>
        <p>
          <strong>Email:</strong> {userEmail || "Yükleniyor..."}
        </p>
        <p>
          <strong>İsim:</strong> {userName || "Yükleniyor"}
        </p>
      </div>
    </div>
  );
};

export default ProfilePage;
