import React, { useState } from 'react';

const HomePage: React.FC = () => {
  const [favorites, setFavorites] = useState<string[]>([]);
  const [username, setUsername] = useState<string>('Kullanıcı'); // Kullanıcı adı

  // Favori ekleme fonksiyonu
  const addToFavorites = (item: string) => {
    if (!favorites.includes(item)) {
      setFavorites([...favorites, item]);
    }
  };

  return (
    <div style={styles.container}>
      {/* Hoşgeldiniz Mesajı */}
      <h1 style={styles.welcomeText}>Hoşgeldiniz, {username}!</h1>

      {/* Ne İzlemek İstersiniz */}
      <h2 style={styles.subHeading}>Ne İzlemek İstersiniz?</h2>
      <div style={styles.buttonContainer}>
        {/* Dizi Butonu */}
        <button
          onClick={() => addToFavorites('Favori Bir Dizi')}
          style={{ ...styles.choiceButton, backgroundColor: '#8e44ad' }}
        >
          Dizi
        </button>
        {/* Film Butonu */}
        <button
          onClick={() => addToFavorites('Favori Bir Film')}
          style={{ ...styles.choiceButton, backgroundColor: '#e74c3c' }}
        >
          Film
        </button>
      </div>

      {/* Favori Listesi */}
      <div style={styles.favoritesContainer}>
        <h2 style={styles.favoritesHeading}>
          Favori Listeniz
          <span style={styles.heartIcon}> ❤️</span>
        </h2>
        {favorites.length > 0 ? (
          <ul style={styles.favoritesList}>
            {favorites.map((item, index) => (
              <li key={index} style={styles.favoriteItem}>
                {item}
              </li>
            ))}
          </ul>
        ) : (
          <p style={styles.noFavoritesText}>
            Henüz favori eklemediniz. Dizi veya film seçerek favori listenizi oluşturabilirsiniz!
          </p>
        )}
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    textAlign: 'center' as const,
    backgroundColor: '#f9f9f9',
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  welcomeText: {
    fontSize: '28px',
    fontWeight: 'bold' as const,
    color: '#2c3e50',
    marginBottom: '20px',
  },
  subHeading: {
    fontSize: '22px',
    marginBottom: '20px',
    color: '#34495e',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    marginBottom: '40px',
  },
  choiceButton: {
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    padding: '20px 40px',
    cursor: 'pointer',
    fontSize: '18px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  },
  favoritesContainer: {
    textAlign: 'left' as const,
    marginTop: '20px',
  },
  favoritesHeading: {
    fontSize: '20px',
    color: '#2c3e50',
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
  },
  heartIcon: {
    fontSize: '24px',
    color: '#e74c3c',
  },
  favoritesList: {
    listStyle: 'none',
    padding: 0,
  },
  favoriteItem: {
    fontSize: '18px',
    marginBottom: '10px',
    color: '#34495e',
  },
  noFavoritesText: {
    fontSize: '16px',
    color: '#7f8c8d',
  },
};

export default HomePage;
