import React, { useEffect, useState } from "react";
import styles from "./CategoriesPage.module.css";

interface Category {
  id: number;
  name: string;
  description?: string;
}

const CategoriesPage: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [error, setError] = useState<string>("");

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch("http://localhost:5114/api/Categories");
        if (!response.ok) {
          throw new Error("Kategoriler alınamadı.");
        }
        const data = await response.json();
        if (Array.isArray(data)) {
          setCategories(data);
        } else {
          setCategories([]); // Eğer veri formatı doğru değilse boş dizi kullan
        }
      } catch (err: any) {
        setError(err.message || "Bir hata oluştu.");
      }
    };

    fetchCategories();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Kategoriler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {(Array.isArray(categories) ? categories : []).map((category) => (
          <div key={category.id} className={styles.card}>
            <h4 className={styles.cardTitle}>{category.name}</h4>
            <p>{category.description || "Açıklama mevcut değil"}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoriesPage;
