import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import LogoutButton from './components/LogoutButton';
import styles from './App.module.css';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage'; // Ana Sayfa Bileşeni

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Kullanıcının giriş durumu

  return (
    <Router>
      <div>
        {/* Navbar sadece giriş yaptıktan sonra gösterilir */}
        {isAuthenticated && <Navbar />}
        <div className={styles.container}>
          <Routes>
            {/* Giriş Sayfası */}
            <Route
              path="/"
              element={
                isAuthenticated ? (
                  <HomePage /> // Giriş yapılmışsa ana sayfa
                ) : (
                  <LoginForm onLoginSuccess={() => setIsAuthenticated(true)} /> // Giriş sayfası
                )
              }
            />
            {/* Kullanıcı giriş yaptıktan sonra açılacak diğer sayfalar */}
            {isAuthenticated && (
              <>
                <Route path="/about" element={<AboutPage />} />
                <Route path="/diziler" element={<SeriesPage />} />
                <Route path="/filmler" element={<MoviesPage />} />
                <Route path="/kategoriler" element={<CategoriesPage />} />
              </>
            )}
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
