import React, { useState } from 'react';

interface Favorite {
  id: number;
  name: string;
  description: string;
}

const HomePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  // Favori ekleme fonksiyonu
  const addToFavorites = (item: Favorite) => {
    if (!favorites.some((fav) => fav.id === item.id)) {
      setFavorites([...favorites, item]);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Hoşgeldiniz!</h1>
      <h2 style={styles.subTitle}>Ne İzlemek İstersiniz?</h2>

      {/* Dizi ve Film Butonları */}
      <div style={styles.buttonsContainer}>
        <button
          style={styles.button}
          onClick={() =>
            addToFavorites({
              id: favorites.length + 1,
              name: 'Favori Bir Dizi',
              description: 'Bu dizi heyecan dolu bir macerayı anlatıyor.',
            })
          }
        >
          Dizi
        </button>
        <button
          style={styles.button}
          onClick={() =>
            addToFavorites({
              id: favorites.length + 2,
              name: 'Favori Bir Film',
              description: 'Bu film ilham verici bir hikayeyi konu alıyor.',
            })
          }
        >
          Film
        </button>
      </div>

      {/* Favoriler Kartları */}
      <div style={styles.favoritesContainer}>
        <h3 style={styles.favoritesTitle}>Favori Listeniz</h3>
        <div style={styles.cardsContainer}>
          {favorites.map((favorite) => (
            <div key={favorite.id} style={styles.card}>
              <div style={styles.cardContent}>
                <p style={styles.cardText}>{favorite.name}</p>
                <div style={styles.cardHover}>
                  <p>{favorite.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: '20px',
    maxWidth: '800px',
    margin: '0 auto',
    textAlign: 'center' as const,
  },
  title: {
    fontSize: '28px',
    fontWeight: 'bold',
    marginBottom: '10px',
    color: '#2c3e50',
  },
  subTitle: {
    fontSize: '22px',
    marginBottom: '20px',
    color: '#34495e',
  },
  buttonsContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    marginBottom: '40px',
  },
  button: {
    backgroundColor: '#3498db',
    color: 'white',
    border: 'none',
    borderRadius: '10px',
    padding: '20px 40px',
    cursor: 'pointer',
    fontSize: '18px',
    transition: 'background-color 0.3s',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
  },
  favoritesContainer: {
    marginTop: '20px',
    textAlign: 'left' as const,
  },
  favoritesTitle: {
    fontSize: '20px',
    marginBottom: '10px',
    color: '#2c3e50',
  },
  cardsContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
    gap: '20px',
  },
  card: {
    position: 'relative' as const,
    borderRadius: '10px',
    overflow: 'hidden',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s',
    cursor: 'pointer',
    backgroundColor: '#ecf0f1',
  },
  cardContent: {
    padding: '20px',
    textAlign: 'center' as const,
  },
  cardText: {
    fontSize: '16px',
    fontWeight: 'bold' as const,
    color: '#34495e',
  },
  cardHover: {
    position: 'absolute' as const,
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.8)',
    color: 'white',
    opacity: 0,
    transition: 'opacity 0.3s',
  },
  card: {
    position: 'relative' as const,
    overflow: 'hidden',
    textAlign: 'center' as const,
  },
  cardHoverVisible: {
    opacity: 1,
  },
};

export default HomePage;
