import React, { useEffect, useState } from 'react';
import { getSeries } from '../services/api'; // API çağrısını import ediyoruz

interface Series {
  id: number;
  name: string;
  description: string;
  releaseDate: string;
}

const SeriesPage: React.FC = () => {
  const [series, setSeries] = useState<Series[]>([]); // Series verileri için state
  const [error, setError] = useState<string>(''); // Hata mesajlarını yönetmek için state

  // Component yüklendiğinde API'den verileri çek
  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const data = await getSeries(); // API'den verileri çekiyoruz
        setSeries(data); // Gelen verileri state'e set ediyoruz
      } catch (err: any) {
        setError(err.message); // Hata olursa hata mesajını set et
      }
    };

    fetchSeries();
  }, []);

  return (
    <div>
      <h1>Diziler</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Hata varsa göster */}
      <ul>
        {series.map((item) => (
          <li key={item.id}>
            <h2>{item.name}</h2>
            <p>{item.description}</p>
            <p><strong>Yayın Tarihi:</strong> {new Date(item.releaseDate).toLocaleDateString()}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SeriesPage;
