import React, { useEffect, useState } from 'react';
import { getSeries } from '../services/api';
import styles from './SeriesPage.module.css';

interface Series {
  id: number;
  title: string;
  description: string;
  numberOfSeasons: number; // Düzeltildi
  numberOfEpisodes: number; // Düzeltildi
  startYear: number; // Düzeltildi
  endYear: number | null; // Bitiş yılı (Devam ediyorsa null olabilir)
}

const SeriesPage: React.FC = () => {
  const [series, setSeries] = useState<Series[]>([]); // Varsayılan değer olarak boş dizi
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const data = await getSeries();
        console.log('Gelen Diziler:', data);
        setSeries(Array.isArray(data) ? data : []); // Gelen veri dizi değilse boş dizi ata
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchSeries();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Diziler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {series.length > 0 ? (
          series.map((item) => (
            <div key={item.id} className={styles.card}>
              <h2 className={styles.cardTitle}>{item.title}</h2> {/* Başta adı göster */}
              <div className={styles.cardHover}>
                <h2 className={styles.hoverTitle}>{item.title}</h2>
                <p><strong>Yıllar:</strong> {item.startYear} - {item.endYear || "Devam Ediyor"}</p>
                <p><strong>Sezon Sayısı:</strong> {item.numberOfSeasons || "Bilgi Yok"}</p>
                <p><strong>Bölüm Sayısı:</strong> {item.numberOfEpisodes || "Bilgi Yok"}</p>
                <p className={styles.description}>{item.description || "Açıklama Yok"}</p> {/* Tarif */}
              </div>
            </div>
          ))
        ) : (
          <p>Henüz dizi verisi bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default SeriesPage;
