import React, { useState, useEffect } from 'react';
import styles from './AdminPanel.module.css';

const AdminPanel: React.FC = () => {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const [moviesRes, seriesRes, categoriesRes] = await Promise.all([
          fetch('http://localhost:5114/api/Movies'),
          fetch('http://localhost:5114/api/Series'),
          fetch('http://localhost:5114/api/Categories'),
        ]);

        if (!moviesRes.ok || !seriesRes.ok || !categoriesRes.ok) {
          throw new Error('Veriler alınamadı');
        }

        const moviesData = await moviesRes.json();
        const seriesData = await seriesRes.json();
        const categoriesData = await categoriesRes.json();

        setMovies(moviesData);
        setSeries(seriesData);
        setCategories(categoriesData.$values || []); // Kategoriler için $values kullanımı
      } catch (err: any) {
        setError('Veriler alınırken bir hata oluştu: ' + err.message);
      }
    };

    fetchAllData();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Admin Paneli</h1>
      {error && <p className={styles.error}>{error}</p>}

      <section className={styles.section}>
        <h2>Filmler</h2>
        {movies.map((movie: any) => (
          <div key={movie.id} className={styles.card}>
            <h4>{movie.title}</h4>
            <button>Sil</button>
            <button>Düzenle</button>
          </div>
        ))}
      </section>

      <section className={styles.section}>
        <h2>Diziler</h2>
        {series.map((serie: any) => (
          <div key={serie.id} className={styles.card}>
            <h4>{serie.title}</h4>
            <button>Sil</button>
            <button>Düzenle</button>
          </div>
        ))}
      </section>

      <section className={styles.section}>
        <h2>Kategoriler</h2>
        {categories.map((category: any) => (
          <div key={category.id} className={styles.card}>
            <h4>{category.name}</h4>
            <button>Sil</button>
            <button>Düzenle</button>
          </div>
        ))}
      </section>
    </div>
  );
};

export default AdminPanel;
