import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';

// Korunan rotalar için bir özel bileşen
const ProtectedRoute: React.FC<{ isAuthenticated: boolean; children: JSX.Element }> = ({ isAuthenticated, children }) => {
  return isAuthenticated ? children : <Navigate to="/" replace />;
};

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Kullanıcı giriş durumu
  const [userEmail, setUserEmail] = useState<string | null>(null); // Kullanıcı email

  // Giriş durumu tarayıcı yeniden yüklendiğinde kontrol edilecek
  useEffect(() => {
    const savedAuth = localStorage.getItem('isAuthenticated');
    const savedEmail = localStorage.getItem('userEmail');
    if (savedAuth === 'true' && savedEmail) {
      setIsAuthenticated(true);
      setUserEmail(savedEmail);
    }
  }, []);

  const handleLogin = (email: string) => {
    setIsAuthenticated(true);
    setUserEmail(email);
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('userEmail', email);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserEmail(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('userEmail');
  };

  return (
    <Router>
      <div>
        {/* Navbar sadece giriş yaptıysa gösterilir */}
        {isAuthenticated && <Navbar onLogout={handleLogout} />}

        <Routes>
          {/* Ana rota: Giriş yapmamışsa LoginForm, giriş yapmışsa HomePage */}
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={(email) => handleLogin(email)}
                  onRegisterClick={() => (window.location.href = '/register')}
                />
              )
            }
          />

          {/* Giriş yapmış kullanıcılar için korunan rotalar */}
          <Route
            path="/about"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <AboutPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/diziler"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <SeriesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/filmler"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <MoviesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/kategoriler"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <CategoriesPage />
              </ProtectedRoute>
            }
          />
          <Route
            path="/profilim"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <ProfilePage userEmail={userEmail || ''} />
              </ProtectedRoute>
            }
          />

          {/* Kayıt sayfası */}
          <Route path="/register" element={<RegisterPage />} />

          {/* Yanlış bir rota girilirse anasayfaya yönlendirin */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
