import React, { useEffect, useState } from 'react';
import { getMovies } from '../services/api'; // API çağrısını import ediyoruz
import styles from './MoviesPage.module.css'; // CSS modülünü import edin

interface Movie {
  id: number;
  title: string; // Film adı
  description: string; // Film açıklaması
  releaseYear: number; // Çıkış yılı
  genre: string; // Tür
  imdbRating: number; // Puan
  duration: string; // Süre
}

const MoviesPage: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const data = await getMovies(); // API'den verileri çekiyoruz
        setMovies(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchMovies();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Filmler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {movies.map((item) => (
          <div key={item.id} className={styles.card}>
            <h2 className={styles.cardTitle}>{item.title}</h2> {/* Başta adı göster */}
            <div className={styles.cardHover}>
              <h2 className={styles.hoverTitle}>{item.title}</h2> {/* Hover durumunda adı tekrar göster */}
              <p><strong>Yıl:</strong> {item.releaseYear || "Bilinmiyor"}</p>
              <p><strong>Tür:</strong> {item.genre || "Bilinmiyor"}</p>
              <p><strong>Puan:</strong> {item.imdbRating || "Puan Yok"}</p>
              <p><strong>Süre:</strong> {item.duration || "Süre Yok"}</p>
              <p className={styles.description}>{item.description || "Açıklama Yok"}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MoviesPage;
