import React, { useState } from 'react';
import { loginUser } from '../services/api';
import background from '../assets/background.jpg'; // Görselin doğru yolu

interface LoginFormProps {
  onLoginSuccess: () => void; // Giriş başarılı olduğunda çağrılacak fonksiyon
}

const LoginForm: React.FC<LoginFormProps> = ({ onLoginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setError('');

    try {
      const data = await loginUser(email, password); // API çağrısı
      console.log('Giriş Başarılı:', data);
      onLoginSuccess(); // Giriş başarılı olduğunda App.tsx'e bilgi gönder
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${background})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh',
        width: '100vw', // Tam genişlik
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        margin: '0', // Ekstra boşlukları sıfırla
        padding: '0', // Ekstra boşlukları sıfırla
      }}
    >
      <form
        onSubmit={handleSubmit}
        style={{
          backgroundColor: 'rgba(255, 255, 255, 0.9)',
          padding: '20px',
          borderRadius: '10px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          width: '320px', // Formu daralt
          textAlign: 'center',
        }}
      >
        <h2 style={{ marginBottom: '20px' }}>Giriş Yap</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              width: '100%', // Tam genişlikte olsun
              padding: '10px',
              borderRadius: '5px',
              border: '1px solid #ccc',
              boxSizing: 'border-box', // Kutuyu düzgün hizalar
            }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Şifre:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              width: '100%', // Tam genişlikte olsun
              padding: '10px',
              borderRadius: '5px',
              border: '1px solid #ccc',
              boxSizing: 'border-box', // Kutuyu düzgün hizalar
            }}
          />
        </div>
        <button
          type="submit"
          style={{
            width: '100%', // Tam genişlikte
            padding: '10px',
            borderRadius: '25px', // Yuvarlak köşeler
            border: 'none',
            backgroundImage: 'linear-gradient(45deg, #6b00b6, #8e2de2)', // Renk geçişi
            color: 'white',
            fontWeight: 'bold',
            cursor: 'pointer',
            transition: 'transform 0.2s ease, box-shadow 0.2s ease',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
          }}
          onMouseOver={(e) => {
            const target = e.currentTarget;
            target.style.transform = 'scale(1.05)';
            target.style.boxShadow = '0 6px 12px rgba(0, 0, 0, 0.2)';
          }}
          onMouseOut={(e) => {
            const target = e.currentTarget;
            target.style.transform = 'scale(1)';
            target.style.boxShadow = '0 4px 6px rgba(0, 0, 0, 0.1)';
          }}
        >
          Giriş Yap
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
