import React, { useState } from 'react';
import axios from 'axios'; // HTTP istekleri için Axios kullanıyoruz
import { useNavigate } from 'react-router-dom'; // Yönlendirme için

const RegisterPage: React.FC = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate(); // Kullanıcıyı yönlendirmek için

  const handleRegister = async (event: React.FormEvent) => {
    event.preventDefault();
    setError('');
    setSuccess(false);

    try {
      // Backend'e POST isteği
      const response = await axios.post('http://localhost:5000/api/Users', {
        name,
        email,
        password,
      });
      console.log('Kullanıcı başarıyla oluşturuldu:', response.data);
      setSuccess(true);

      // Başarılıysa giriş sayfasına yönlendirme
      setTimeout(() => {
        navigate('/');
      }, 2000); // 2 saniye bekleyip giriş sayfasına yönlendir
    } catch (err: any) {
      console.error('Kullanıcı oluşturulamadı:', err);
      setError('Kullanıcı oluşturulurken bir hata oluştu. Lütfen tekrar deneyin.');
    }
  };

  return (
    <div
      style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        background: '#f5f5f5',
        padding: '20px',
      }}
    >
      <div
        style={{
          backgroundColor: 'white',
          padding: '30px',
          borderRadius: '10px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
          width: '400px',
          textAlign: 'center',
        }}
      >
        <h1 style={{ fontFamily: 'Arial, sans-serif', color: '#6b00b6', marginBottom: '20px' }}>
          Kayıt Ol
        </h1>
        {error && <p style={{ color: 'red', marginBottom: '15px' }}>{error}</p>}
        {success && <p style={{ color: 'green', marginBottom: '15px' }}>Kayıt başarılı! Giriş sayfasına yönlendiriliyorsunuz...</p>}

        <form onSubmit={handleRegister}>
          <div style={{ marginBottom: '15px' }}>
            <input
              type="text"
              placeholder="Adınız"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: '5px',
                border: '1px solid #ccc',
              }}
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: '5px',
                border: '1px solid #ccc',
              }}
            />
          </div>
          <div style={{ marginBottom: '15px' }}>
            <input
              type="password"
              placeholder="Şifre"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{
                width: '100%',
                padding: '10px',
                borderRadius: '5px',
                border: '1px solid #ccc',
              }}
            />
          </div>
          <button
            type="submit"
            style={{
              width: '100%',
              padding: '10px',
              backgroundColor: '#6b00b6',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px',
              fontWeight: 'bold',
            }}
          >
            Kayıt Ol
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegisterPage;
