import React, { useEffect, useState } from 'react';
import { getSeries } from '../services/api'; // API çağrısını import ediyoruz
import styles from './SeriesPage.module.css'; // CSS modülünü import edin

interface Series {
  id: number;
  title: string; // Dizi başlığı
  description: string; // Dizi açıklaması
  NumberOfSeasons: number; // Sezon sayısı
  NumberOfEpisodes: number; // Bölüm sayısı
  StartYear: number; // Başlangıç yılı
  EndYear: number; // Bitiş yılı
}

const SeriesPage: React.FC = () => {
  const [series, setSeries] = useState<Series[]>([]);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const data = await getSeries();
        console.log(data); // Gelen veriyi kontrol etmek için konsola yazdırıyoruz
        setSeries(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchSeries();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Diziler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {series.map((item) => (
          <div key={item.id} className={styles.card}>
            <h2 className={styles.cardTitle}>{item.title}</h2> {/* Başta adı göster */}
            <div className={styles.cardHover}>
              <h2 className={styles.hoverTitle}>{item.title}</h2> {/* Hover durumunda adı tekrar göster */}
              <p><strong>Yıllar:</strong> {item.StartYear} - {item.EndYear || "Devam Ediyor"}</p>
              <p><strong>Sezon Sayısı:</strong> {item.NumberOfSeasons || "Bilgi Yok"}</p>
              <p><strong>Bölüm Sayısı:</strong> {item.NumberOfEpisodes || "Bilgi Yok"}</p>
              <p className={styles.description}>{item.description || "Açıklama Yok"}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SeriesPage;
