import React, { useEffect, useState } from 'react';
import './ProfilePage.css';

interface UserProfile {
  name: string;
  email: string;
}

const ProfilePage: React.FC = () => {
  const [user, setUser] = useState<UserProfile | null>(null);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch('http://localhost:5114/api/Users/GetUserDetails', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Token ile doğrulama
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || 'Kullanıcı bilgisi alınamadı.');
        }

        const data: UserProfile = await response.json();
        setUser(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchUserProfile();
  }, []);

  return (
    <div className="profile-container">
      {error ? (
        <p className="error">{error}</p>
      ) : user ? (
        <div className="profile-card">
          <h1>Profil Bilgileri</h1>
          <p>
            <strong>Ad:</strong> {user.name}
          </p>
          <p>
            <strong>Email:</strong> {user.email}
          </p>
        </div>
      ) : (
        <p className="error">Yükleniyor...</p>
      )}
    </div>
  );
};

export default ProfilePage;
