import React, { useEffect, useState } from 'react';
import styles from './ProfilePage.css'; // Profil sayfası için CSS dosyanız

interface User {
  id: number;
  name: string;
  email: string;
}

const ProfilePage: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:5114/api/Users/GetUserDetails'); // Backend URL'i
        if (!response.ok) {
          throw new Error('Kullanıcı bilgileri yüklenemedi.');
        }
        const data = await response.json();
        setUser(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchUser();
  }, []);

  return (
    <div className={styles['profile-container']}>
      <div className={styles['profile-card']}>
        {error ? (
          <p className={styles.error}>{error}</p>
        ) : user ? (
          <>
            <h1>Profil Bilgileri</h1>
            <p><strong>Ad:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
          </>
        ) : (
          <p>Bilgiler yükleniyor...</p>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
