import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './HomePage.css';
import { getFeaturedFavorites } from '../services/api'; // Öne çıkan filmleri çekmek için API çağrısı
import background from '../assets/background.jpg';

// FontAwesome İkonları
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';

interface Favorite {
  id: number;
  title: string;
  description: string;
}

const HomePage: React.FC = () => {
  const navigate = useNavigate();
  const [featuredFavorites, setFeaturedFavorites] = useState<Favorite[]>([]);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchFeaturedFavorites = async () => {
      try {
        const data = await getFeaturedFavorites(); // Öne çıkan içerikleri alıyoruz
        setFeaturedFavorites(data);
      } catch (err: any) {
        setError(err.message || 'Öne çıkan içerikler yüklenemedi.');
      }
    };

    fetchFeaturedFavorites();
  }, []);

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      { breakpoint: 768, settings: { slidesToShow: 1 } },
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
    ],
  };

  return (
    <div className="homepage-container" style={{ backgroundImage: `url(${background})` }}>
      <h1 className="homepage-title">Hoşgeldiniz!</h1>
      <h2 className="homepage-subtitle">Ne İzlemek İstersiniz?</h2>
      <h3 className="featured-title">Editörün Seçimleri</h3>

      {/* Slider */}
      <div className="slider-section">
        {error ? (
          <p className="error">{error}</p>
        ) : (
          <Slider {...sliderSettings} className="slider-container">
            {featuredFavorites.map((item) => (
              <div key={item.id} className="card">
                <h3>{item.title}</h3>
                <p>{item.description}</p>
                <button
                  className="add-favorite-button"
                  onClick={() => alert(`"${item.title}" favorilere eklendi!`)}
                >
                  <FontAwesomeIcon icon={faHeart} className="heart-icon" />
                </button>
              </div>
            ))}
          </Slider>
        )}
      </div>

      {/* Kategoriler */}
      <div className="categories-section">
        <button className="category-button" onClick={() => navigate('/diziler')}>
          Diziler
        </button>
        <button className="category-button" onClick={() => navigate('/filmler')}>
          Filmler
        </button>
      </div>
    </div>
  );
};

export default HomePage;
