import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Kullanıcı giriş durumu

  // Sayfa yüklendiğinde token'i kontrol et
  useEffect(() => {
    const token = document.cookie
      .split('; ')
      .find(row => row.startsWith('token=')) // Token'i çerezlerden al
      ?.split('=')[1]; // Token'in değerini ayır

    if (token) {
      // Token varsa oturum açık
      setIsAuthenticated(true);
    } else {
      // Token yoksa oturum kapalı
      setIsAuthenticated(false);
    }
  }, []);

  return (
    <Router>
      <div>
        {/* Navbar sadece giriş yaptıysa gösterilir */}
        {isAuthenticated && <Navbar />}
        <Routes>
          {/* Giriş yapılmamışsa LoginForm göster */}
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={() => setIsAuthenticated(true)}
                  onRegisterClick={() => (window.location.href = '/register')}
                />
              )
            }
          />

          {/* Kayıt sayfası */}
          <Route path="/register" element={<RegisterPage />} />

          {/* Giriş yapılmışsa erişilebilecek sayfalar */}
          {isAuthenticated ? (
            <>
              <Route path="/about" element={<AboutPage />} />
              <Route path="/diziler" element={<SeriesPage />} />
              <Route path="/filmler" element={<MoviesPage />} />
              <Route path="/kategoriler" element={<CategoriesPage />} />
              <Route path="/profilim" element={<ProfilePage />} />
            </>
          ) : (
            // Giriş yapılmamışsa yönlendirme
            <Route path="*" element={<Navigate to="/" replace />} />
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
