import React, { useState } from 'react';
import './HomePage.css'; // CSS dosyasını içeri aktarıyoruz
import backgroundImage from '../assets/deneme.jpg'; // Resmi import ediyoruz

interface Favorite {
  id: number;
  name: string;
  description: string;
}

const HomePage: React.FC = () => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  // Favori ekleme fonksiyonu
  const addToFavorites = (item: Favorite) => {
    if (!favorites.some((fav) => fav.id === item.id)) {
      setFavorites([...favorites, item]);
    }
  };

  return (
    <div
      className="homepage-container"
      style={{ backgroundImage: `url(${backgroundImage})` }} // Arka plan resmi eklendi
    >
      <h1 className="homepage-title">Hoşgeldiniz!</h1>
      <h2 className="homepage-subtitle">Ne İzlemek İstersiniz?</h2>

      {/* Dizi ve Film Butonları */}
      <div className="button-container">
        <button
          className="choice-button dizi"
          onClick={() =>
            addToFavorites({
              id: favorites.length + 1,
              name: 'Favori Bir Dizi',
              description: 'Bu dizi heyecan dolu bir macerayı anlatıyor.',
            })
          }
        >
          Dizi
        </button>
        <button
          className="choice-button film"
          onClick={() =>
            addToFavorites({
              id: favorites.length + 2,
              name: 'Favori Bir Film',
              description: 'Bu film ilham verici bir hikayeyi konu alıyor.',
            })
          }
        >
          Film
        </button>
      </div>

      {/* Favoriler Kartları */}
      <div className="favorites-container">
        <h3 className="favorites-title">Favori Listeniz</h3>
        <div className="cards-container">
          {favorites.map((favorite) => (
            <div key={favorite.id} className="card">
              <p className="card-name">{favorite.name}</p>
              <div className="card-hover">
                <p>{favorite.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
