import React, { useState } from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './HomePage.css';

interface HomePageProps {
  userName: string;
}

interface Favorite {
  id: number;
  name: string;
  description: string;
  image: string;
}

const HomePage: React.FC<HomePageProps> = ({ userName }) => {
  const [favorites, setFavorites] = useState<Favorite[]>([]);

  const addToFavorites = (item: Favorite) => {
    if (!favorites.some((fav) => fav.id === item.id)) {
      setFavorites([...favorites, item]);
    }
  };

  const moviesAndShows = [
    {
      id: 1,
      name: 'Breaking Bad',
      description: 'Bu dizi heyecan dolu bir macerayı anlatıyor.',
      image: 'https://via.placeholder.com/200',
    },
    {
      id: 2,
      name: 'Inception',
      description: 'Bu film ilham verici bir hikayeyi konu alıyor.',
      image: 'https://via.placeholder.com/200',
    },
    {
      id: 3,
      name: 'Stranger Things',
      description: 'Fantastik bir dünyanın kapılarını açıyor.',
      image: 'https://via.placeholder.com/200',
    },
    {
      id: 4,
      name: 'The Dark Knight',
      description: 'Efsane bir kahramanın hikayesi.',
      image: 'https://via.placeholder.com/200',
    },
  ];

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      { breakpoint: 768, settings: { slidesToShow: 1 } },
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
    ],
  };

  return (
    <div className="homepage-container">
      <h1 className="homepage-title">Hoşgeldiniz, <span>{userName}</span>!</h1>
      <h2 className="homepage-subtitle">Ne İzlemek İstersiniz?</h2>

      <Slider {...sliderSettings} className="slider-container">
        {moviesAndShows.map((item) => (
          <div key={item.id} className="card">
            <img src={item.image} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <button
              className="add-favorite-button"
              onClick={() => addToFavorites(item)}
            >
              Favorilere Ekle
            </button>
          </div>
        ))}
      </Slider>

      <div className="favorites-container">
        <h3 className="favorites-title">Favori Listeniz</h3>
        <div className="cards-container">
          {favorites.map((favorite) => (
            <div key={favorite.id} className="card">
              <p className="card-name">{favorite.name}</p>
              <div className="card-hover">
                <p>{favorite.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
