import React, { useState } from 'react';
import styles from './AdminPanel.module.css';

const AdminPanel: React.FC = () => {
  const [response, setResponse] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleRequest = async (method: string, endpoint: string) => {
    setResponse(null);
    setError(null);

    try {
      const response = await fetch(`http://localhost:5114${endpoint}`, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: method === 'POST' || method === 'PUT' ? JSON.stringify({}) : null, // POST ve PUT için dummy veri gönderiyoruz
      });

      if (!response.ok) {
        throw new Error(`Hata: ${response.status} - ${response.statusText}`);
      }

      const data = await response.json();
      setResponse(JSON.stringify(data, null, 2));
    } catch (err: any) {
      setError(err.message || 'Bilinmeyen bir hata oluştu.');
    }
  };

  const endpoints = [
    { method: 'GET', endpoint: '/api/Categories' },
    { method: 'POST', endpoint: '/api/Categories' },
    { method: 'GET', endpoint: '/api/Categories/1' },
    { method: 'PUT', endpoint: '/api/Categories/1' },
    { method: 'DELETE', endpoint: '/api/Categories/1' },
    { method: 'GET', endpoint: '/api/Movies' },
    { method: 'POST', endpoint: '/api/Movies' },
    { method: 'GET', endpoint: '/api/Movies/1' },
    { method: 'PUT', endpoint: '/api/Movies/1' },
    { method: 'DELETE', endpoint: '/api/Movies/1' },
    { method: 'GET', endpoint: '/api/Series' },
    { method: 'POST', endpoint: '/api/Series' },
    { method: 'GET', endpoint: '/api/Series/1' },
    { method: 'PUT', endpoint: '/api/Series/1' },
    { method: 'DELETE', endpoint: '/api/Series/1' },
    { method: 'GET', endpoint: '/api/Users' },
    { method: 'POST', endpoint: '/api/Users' },
    { method: 'GET', endpoint: '/api/Users/1' },
    { method: 'PUT', endpoint: '/api/Users/1' },
    { method: 'DELETE', endpoint: '/api/Users/1' },
    // Daha fazla endpoint eklenebilir
  ];

  return (
    <div className={styles.adminPanel}>
      <h1 className={styles.title}>Admin Paneli</h1>
      <div className={styles.endpoints}>
        {endpoints.map((ep, index) => (
          <button
            key={index}
            className={styles.endpointButton}
            onClick={() => handleRequest(ep.method, ep.endpoint)}
          >
            {ep.method} - {ep.endpoint}
          </button>
        ))}
      </div>
      <div className={styles.responseContainer}>
        <h2>Sonuç</h2>
        {response && <pre className={styles.response}>{response}</pre>}
        {error && <pre className={styles.error}>{error}</pre>}
      </div>
    </div>
  );
};

export default AdminPanel;
