import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Kullanıcı giriş durumu
  const [userEmail, setUserEmail] = useState("ahmet@example.com"); // Kullanıcı email

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserEmail('');
    // Ekstra olarak localStorage veya başka bir oturum temizleme işlemi yapılabilir.
  };

  return (
    <Router>
      <div>
        {/* Navbar sadece giriş yaptıysa gösterilir */}
        {isAuthenticated && (
          <Navbar
            onLogout={handleLogout}
            userEmail={userEmail} // Kullanıcının e-posta adresini gönderiyoruz
          />
        )}

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={() => setIsAuthenticated(true)}
                  onRegisterClick={() => (window.location.href = '/register')}
                />
              )
            }
          />
          {isAuthenticated && (
            <>
              <Route path="/about" element={<AboutPage />} />
              <Route path="/diziler" element={<SeriesPage />} />
              <Route path="/filmler" element={<MoviesPage />} />
              <Route path="/kategoriler" element={<CategoriesPage />} />
              <Route path="/profilim" element={<ProfilePage />} />
            </>
          )}
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
