import React, { useState } from 'react';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import LogoutButton from './components/LogoutButton';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // Kullanıcının giriş durumu

  return (
    <div style={{ margin: '20px' }}>
      <h1>SFlix Giriş Sistemi</h1>
      {isAuthenticated ? ( // Giriş yapıldıysa LogoutButton göster
        <div>
          <p>Hoşgeldiniz!</p>
          <LogoutButton />
        </div>
      ) : (
        <LoginForm onLoginSuccess={() => setIsAuthenticated(true)} /> // Login başarılıysa state'i güncelle
      )}
    </div>
  );
};

export default App;
