import React, { useEffect, useState } from 'react';
import { getSeries } from '../services/api';
import styles from './SeriesPage.module.css';

interface Series {
  id: number;
  name: string;
  description: string;
  seasons: number;
  episodes: number;
  coverImage: string; // Kapak fotoğrafı
}

const SeriesPage: React.FC = () => {
  const [series, setSeries] = useState<Series[]>([]);
  const [error, setError] = useState<string>('');
  const [expandedCard, setExpandedCard] = useState<number | null>(null); // Hangi kartın genişletildiğini tutar

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const data = await getSeries();
        setSeries(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchSeries();
  }, []);

  // Kart tıklama olayını işleyen fonksiyon
  const handleCardClick = (id: number) => {
    setExpandedCard((prevId) => (prevId === id ? null : id)); // Eğer aynı karta tıklanırsa kapat
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Diziler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {series.map((item) => (
          <div
            key={item.id}
            className={`${styles.card} ${
              expandedCard === item.id ? styles.expanded : ''
            }`}
            onClick={() => handleCardClick(item.id)} // Tıklama olayını bağlama
          >
            <img
              src={item.coverImage}
              alt={`${item.name} Kapak`}
              className={styles.cardImage}
            />
            <h2 className={styles.cardTitle}>{item.name}</h2>
            {expandedCard === item.id && ( // Eğer kart genişletilmişse, detayları göster
              <div className={styles.cardDetails}>
                <p className={styles.cardDescription}>{item.description}</p>
                <p><strong>Sezon Sayısı:</strong> {item.seasons}</p>
                <p><strong>Bölüm Sayısı:</strong> {item.episodes}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default SeriesPage;
