import React, { useEffect, useState } from 'react';
import { getMovies } from '../services/api'; // API çağrısını import ediyoruz
import styles from './MoviesPage.module.css'; // CSS modülünü import edin

// Film için gerekli arayüz
interface Movie {
  id: number;
  title: string;
  description: string;
  releaseYear: number;
  genre: string;
  imdbRating: number;
  duration: string;
}

const MoviesPage: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]); // Boş bir dizi olarak başlat
  const [error, setError] = useState<string>(''); // Hata durumu

  // Verileri API'den çek
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const data = await getMovies(); // API'den verileri çek
        console.log('Gelen Filmler:', data); // Gelen verileri kontrol et
        // Gelen verinin dizi olup olmadığını kontrol et
        if (Array.isArray(data)) {
          setMovies(data);
        } else {
          console.error('Beklenmeyen veri formatı:', data);
          setMovies([]); // Veri formatı hatalıysa boş bir dizi ata
        }
      } catch (err: any) {
        console.error('Hata:', err.message);
        setError('Filmleri yüklerken bir hata oluştu.');
      }
    };

    fetchMovies(); // useEffect içinde API çağrısını tetikle
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Filmler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {/* Veriler doluysa kartları göster */}
        {movies.length > 0 ? (
          movies.map((item) => (
            <div key={item.id} className={styles.card}>
              <h2 className={styles.cardTitle}>{item.title}</h2>
              <div className={styles.cardHover}>
                <h2 className={styles.hoverTitle}>{item.title}</h2>
                <p>
                  <strong>Yıl:</strong> {item.releaseYear || 'Bilinmiyor'}
                </p>
                <p>
                  <strong>Tür:</strong> {item.genre || 'Bilinmiyor'}
                </p>
                <p>
                  <strong>Puan:</strong> {item.imdbRating || 'Puan Yok'}
                </p>
                <p>
                  <strong>Süre:</strong> {item.duration || 'Süre Yok'}
                </p>
                <p className={styles.description}>
                  {item.description || 'Açıklama Yok'}
                </p>
              </div>
            </div>
          ))
        ) : (
          <p>Henüz bilgi yok.</p> // Eğer veri yoksa gösterilecek mesaj
        )}
      </div>
    </div>
  );
};

export default MoviesPage;
