import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage';
import AboutPage from './pages/AboutPage';
import HomePage from './pages/HomePage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';
import AdminPanel from './pages/AdminPanel';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  // Uygulama yüklendiğinde token'ı kontrol et
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
      // Admin durumu kontrolü (örnek için basit bir fetch)
      fetch('http://localhost:5114/api/Users/GetUserDetails', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (response.ok) {
            return response.json();
          } else {
            throw new Error('Yetkilendirme hatası');
          }
        })
        .then((data) => {
          setIsAdmin(data.isAdmin); // Eğer backend'den admin bilgisi geliyorsa
        })
        .catch(() => {
          setIsAuthenticated(false); // Token geçersizse kullanıcıyı oturumdan çıkar
          localStorage.removeItem('token');
        });
    }
  }, []);

  // Çıkış yapıldığında çağrılan fonksiyon
  const handleLogout = () => {
    setIsAuthenticated(false);
    setIsAdmin(false);
    localStorage.removeItem('token'); // Token'ı temizle
  };

  return (
    <Router>
      <div>
        {isAuthenticated && <Navbar isAdmin={isAdmin} onLogout={handleLogout} />}

        <div style={{ textAlign: 'center', margin: '20px' }}>
          {isAuthenticated && isAdmin && (
            <Link to="/admin-panel">
              <button style={{ padding: '10px 20px', fontSize: '16px' }}>Admin Paneline Git</button>
            </Link>
          )}
        </div>

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <HomePage />
              ) : (
                <LoginForm
                  onLoginSuccess={(isAdmin) => {
                    setIsAuthenticated(true);
                    setIsAdmin(isAdmin);
                  }}
                  onRegisterClick={() => (window.location.href = '/register')}
                />
              )
            }
          />
          {isAuthenticated && (
            <>
              <Route path="/about" element={<AboutPage />} />
              <Route path="/diziler" element={<SeriesPage />} />
              <Route path="/filmler" element={<MoviesPage />} />
              <Route path="/kategoriler" element={<CategoriesPage />} />
              <Route path="/profilim" element={<ProfilePage />} />
              {isAdmin && <Route path="/admin-panel" element={<AdminPanel />} />}
            </>
          )}
          <Route path="/register" element={<RegisterPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
