import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import LogoutButton from './components/LogoutButton';
import styles from './App.module.css';
import SeriesPage from './pages/SeriesPage';
import MoviesPage from './pages/MoviesPage';
import CategoriesPage from './pages/CategoriesPage'; // Kategoriler sayfasını ekleyin
import AboutPage from './pages/AboutPage'; // Hakkımızda sayfasını import edin
const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <Router>
      <div>
        <Navbar /> {/* Navbar her sayfa için sabit */}
        <div className={styles.container}>
          <Routes>
            <Route path="/" element={isAuthenticated ? <LogoutButton /> : <LoginForm onLoginSuccess={() => setIsAuthenticated(true)} />} />
            <Route path="/about" element={<AboutPage />} /> {/* Hakkımızda sayfası */}
            <Route path="/diziler" element={<SeriesPage />} />
            <Route path="/filmler" element={<MoviesPage />} />
            <Route path="/kategoriler" element={<CategoriesPage />} /> {/* Kategoriler için rota */}
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
