import React, { useEffect, useState } from 'react';
import { getSeries } from '../services/api'; // API çağrısını import ediyoruz
import styles from './SeriesPage.module.css'; // CSS modülü

interface Series {
  id: number;
  name: string;
  description: string;
  seasons: number;
  episodes: number;
}

const SeriesPage: React.FC = () => {
  const [series, setSeries] = useState<Series[]>([]);
  const [error, setError] = useState<string>('');

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const data = await getSeries(); // Backend'den verileri çekiyoruz
        setSeries(data);
      } catch (err: any) {
        setError(err.message);
      }
    };

    fetchSeries();
  }, []);

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Diziler</h1>
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.grid}>
        {series.map((item) => (
          <div key={item.id} className={styles.card}>
            {/* Kart Başlangıç Görünümü */}
            <h2 className={styles.cardTitle}>{item.name}</h2>

            {/* Hover Detayları */}
            <div className={styles.cardHover}>
              <p><strong>Sezon Sayısı:</strong> {item.seasons}</p>
              <p><strong>Bölüm Sayısı:</strong> {item.episodes}</p>
              <p className={styles.description}>{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SeriesPage;
