import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './CategoriesPage.module.css';

interface Category {
  id: number;
  name: string;
}

const CategoriesPage: React.FC = () => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [error, setError] = useState<string>('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch('http://localhost:5114/api/Categories');
        const data = await response.json();
        setCategories(data);
      } catch (err: any) {
        setError('Kategoriler alınırken bir hata oluştu: ' + err.message);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryClick = (categoryId: number) => {
    // Kategoriye ait sayfaya yönlendirme
    navigate(`/kategori/${categoryId}`);
  };

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>Kategoriler</h1>
      {error && <p className={styles.error}>{error}</p>}

      <div className={styles.categoriesGrid}>
        {categories.map((category) => (
          <button
            key={category.id}
            className={styles.categoryButton}
            onClick={() => handleCategoryClick(category.id)}
          >
            {category.name}
          </button>
        ))}
      </div>
    </div>
  );
};

export default CategoriesPage;
