import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Router bileşenleri
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import LogoutButton from './components/LogoutButton';
import styles from './App.module.css';
import SeriesPage from './pages/SeriesPage'; // Diziler sayfasını import ediyoruz

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  return (
    <Router>
      <div className={styles.appContainer}> {/* Genel kapsayıcı */}
        <Navbar /> {/* Navbar sabit bir şekilde tüm sayfalarda görünecek */}
        <div className={styles.content}> {/* Ana içerik alanı */}
          <Routes>
            {/* Diziler Sayfası */}
            <Route path="/diziler" element={<SeriesPage />} />

            {/* Ana Sayfa */}
            <Route
              path="/"
              element={
                isAuthenticated ? (
                  <div>
                    <h1>SFlix Giriş Sistemi</h1>
                    <p>Hoşgeldiniz!</p>
                    <LogoutButton />
                  </div>
                ) : (
                  <LoginForm onLoginSuccess={() => setIsAuthenticated(true)} />
                )
              }
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
